import { Component } from 'react'

import './carrousel.css'

class Carrousel extends Component {
  constructor(props) {
    super(props)
    this.state = {
      classNameDropContent: 'close',
      classNameArrow: '',
    }
  }

  handleClick() {
    console.log()
  }

  render() {
    return (
      <div className="carrousel">
        <div className="navigation">
          <button
            type="button"
            className="nav nav--left"
            id="left"
            aria-label="Retour à l'image précédente."
            onClick={() => this.handleClick('right')}
          ></button>
          <button
            type="button"
            className="nav nav--right"
            id="right"
            aria-label="Aller à l'image suivante."
            onClick={this.handleClick}
          ></button>
        </div>
        <figure>
          <img src={this.props.pictures[0]} alt={this.props.title} />
        </figure>
        <span>{`1/${this.props.pictures.length}`}</span>
      </div>
    )
  }
}

export default Carrousel
