import { Component } from 'react'
import Carrousel from '../../components/Carrousel'
import Tag from '../../components/Tag'
import Profil from '../../components/Profil'
import Rate from '../../components/Rate'

import { dataAppart } from '../../data/dataAppart'

import './detailsAppart.css'

class DetailsAppart extends Component {
  id = this.props.match.params
  result = dataAppart.filter((el) => {
    if (el.id === this.id) {
      return el
    } else {
      return false
    }
  })

  render() {
    return (
      <main className="detailsAppart">
        <Carrousel
          pictures={this.result[0].pictures}
          title={this.result[0].title}
        />
        <div className="container">
          <div className="info_1">
            <h1>{this.result[0].title}</h1>
            <h2>{this.result[0].location}</h2>
            <div className="tags">
              {this.result[0].tags.map((el) => (
                <Tag key={el} value={el} />
              ))}
            </div>
          </div>
          <div className="info_2">
            <Rate />
            <Profil
              name={this.result[0].host.name}
              photo={this.result[0].host.picture}
            />
          </div>
        </div>
        <div className="container"></div>
      </main>
    )
  }
}

export default DetailsAppart
