import { Component } from 'react'
import Carrousel from '../../components/Carrousel'
import Tag from '../../components/Tag'
import Profil from '../../components/Profil'
import Rate from '../../components/Rate'

import { dataAppart } from '../../data/dataAppart'

import './detailsAppart.css'

class DetailsAppart extends Component {
  constructor(props) {
    super(props)
    const data = dataAppart.filter((el) => {
      if (el.id === this.props.match.params) {
        return el
      } else {
        return false
      }
    })
    this.state = {
      result: [],
    }
  }

  getResult() {
    this.setState((prevState) => ({
      result: [...prevState.result, data],
    }))
  }

  componentDidMount() {
    this.getResult()
  }

  render() {
    console.log(this.state.result)
    return (
      <main className="detailsAppart">
        <Carrousel
          pictures={this.state.result[0].pictures}
          title={this.state.result[0].title}
        />
        <div className="container">
          <div className="info_1">
            <h1>{this.state.result[0].title}</h1>
            <h2>{this.state.result[0].location}</h2>
            <div className="tags">
              {this.state.result[0].tags.map((el) => (
                <Tag key={el} value={el} />
              ))}
            </div>
          </div>
          <div className="info_2">
            <Rate />
            <Profil
              name={this.state.result[0].host.name}
              photo={this.state.result[0].host.picture}
            />
          </div>
        </div>
        <div className="container"></div>
      </main>
    )
  }
}

export default DetailsAppart
